pragma solidity ^0.8.0;

import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import {IERC1271} from "@openzeppelin/contracts/interfaces/IERC1271.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";

import {IERC721Permit} from "../interfaces/IERC721Permit.sol";

abstract contract ERC721Permit is ERC721, IERC721Permit, EIP712 {
    error InvalidSignature();
    error PermitExpired();

    // keccak256("Permit(address spender,uint256 tokenId,uint256 nonce,uint256 deadline)");
    bytes32 public constant override PERMIT_TYPEHASH =
        0x49ecf333e5b8c95c40fdafc95c1ad136e8914a8fb55e9dc8bb01eaa83a2df9ad;

    constructor(string memory name_, string memory symbol_, string memory version_)
        ERC721(name_, symbol_)
        EIP712(name_, version_)
    {}

    function permit(address spender, uint256 tokenId, uint256 deadline, uint8 v, bytes32 r, bytes32 s)
        external
        payable
        override
    {
        if (block.timestamp > deadline) revert PermitExpired();

        bytes32 structHash =
            keccak256(abi.encode(PERMIT_TYPEHASH, spender, tokenId, _getAndIncrementNonce(tokenId), deadline));
        bytes32 digest = _hashTypedDataV4(structHash);

        address owner = ownerOf(tokenId);
        if (spender == owner) revert InvalidSignature();

        if (Address.isContract(owner)) {
            if (IERC1271(owner).isValidSignature(digest, abi.encodePacked(r, s, v)) != 0x1626ba7e) {
                revert InvalidSignature();
            }
        } else {
            address signer = ECDSA.recover(digest, v, r, s);
            if (signer != owner) revert InvalidSignature();
        }

        _approve(spender, tokenId);
    }

    function DOMAIN_SEPARATOR() public view override returns (bytes32) {
        return _domainSeparatorV4();
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, IERC165) returns (bool) {
        return interfaceId == type(IERC721Permit).interfaceId || super.supportsInterface(interfaceId);
    }

    function _getAndIncrementNonce(uint256 tokenId) internal virtual returns (uint256);
}
","keccak256":"0xabf0f7ae18976e6e1691c1a61aff57cfc0a65422e8b843e9c5372822b15de2cd","license":"GPL-2.0-or-later"},"contracts/libraries/Epoch.sol":{"content":"// SPDX-License-Identifier: -
// License: https://license.clober.io/LICENSE.pdf

pragma solidity ^0.8.0;

type Epoch is uint8;

using {gt as >, gte as >=, lt as <, lte as <=, eq as ==} for Epoch global;

function gt(Epoch a, Epoch b) pure returns (bool) {
    return Epoch.unwrap(a) > Epoch.unwrap(b);
}

function gte(Epoch a, Epoch b) pure returns (bool) {
    return Epoch.unwrap(a) >= Epoch.unwrap(b);
}

function lt(Epoch a, Epoch b) pure returns (bool) {
    return Epoch.unwrap(a) < Epoch.unwrap(b);
}

function lte(Epoch a, Epoch b) pure returns (bool) {
    return Epoch.unwrap(a) <= Epoch.unwrap(b);
}

function eq(Epoch a, Epoch b) pure returns (bool) {
    return Epoch.unwrap(a) == Epoch.unwrap(b);
}

library EpochLibrary {
    using EpochLibrary for Epoch;

    error EpochOverflow();

    uint256 internal constant MONTHS_PER_EPOCH = 6;
    uint256 constant SECONDS_PER_DAY = 24 * 60 * 60;
    int256 constant OFFSET19700101 = 2440588;

    function wrap(uint8 epoch) internal pure returns (Epoch) {
        return Epoch.wrap(epoch);
    }

    function unwrap(Epoch epoch) internal pure returns (uint8) {
        return Epoch.unwrap(epoch);
    }

    function startTime(Epoch epoch) internal pure returns (uint256) {
        uint8 currentEpoch = Epoch.unwrap(epoch);
        if (currentEpoch == 0) return 0;
        unchecked {
            return _epochToTimestamp(currentEpoch - 1);
        }
    }

    function endTime(Epoch epoch) internal pure returns (uint256) {
        return _epochToTimestamp(Epoch.unwrap(epoch));
    }

    function isExpired(Epoch epoch) internal view returns (bool) {
        return endTime(epoch) <= block.timestamp;
    }

    function lastExpiredEpoch() internal view returns (Epoch) {
        return current().sub(1);
    }

    function current() internal view returns (Epoch) {
        return Epoch.wrap(_timestampToEpoch(block.timestamp));
    }

    function long(Epoch epoch) internal pure returns (uint256) {
        unchecked {
            return endTime(epoch) - startTime(epoch);
        }
    }

    function add(Epoch epoch, uint8 epochs) internal pure returns (Epoch) {
        return Epoch.wrap(Epoch.unwrap(epoch) + epochs);
    }

    function sub(Epoch epoch, uint8 epochs) internal pure returns (Epoch) {
        return Epoch.wrap(Epoch.unwrap(epoch) - epochs);
    }

    function sub(Epoch e1, Epoch e2) internal pure returns (uint8) {
        return Epoch.unwrap(e1) - Epoch.unwrap(e2);
    }

    function max(Epoch a, Epoch b) internal pure returns (Epoch) {
        return a > b ? a : b;
    }

    // Inspired by https://github.com/bokkypoobah/BokkyPooBahsDateTimeLibrary/blob/master/contracts/BokkyPooBahsDateTimeLibrary.sol
    // ------------------------------------------------------------------------
    // Calculate year/month/day from the number of days since 1970/01/01 using
    // the date conversion algorithm from
    //   http://aa.usno.navy.mil/faq/docs/JD_Formula.php
    // and adding the offset 2440588 so that 1970/01/01 is day 0
    //
    // int L = days + 68569 + offset
    // int N = 4 * L / 146097
    // L = L - (146097 * N + 3) / 4
    // year = 4000 * (L + 1) / 1461001
    // L = L - 1461 * year / 4 + 31
    // month = 80 * L / 2447
    // dd = L - 2447 * month / 80
    // L = month / 11
    // month = month + 2 - 12 * L
    // year = 100 * (N - 49) + year + L
    // ------------------------------------------------------------------------
    function _timestampToEpoch(uint256 timestamp) private pure returns (uint8) {
        unchecked {
            uint256 _days = timestamp / SECONDS_PER_DAY;
            int256 __days = int256(_days);

            int256 L = __days + 68569 + OFFSET19700101;
            int256 N = (4 * L) / 146097;
            L = L - (146097 * N + 3) / 4;
            int256 _year = (4000 * (L + 1)) / 1461001;
            L = L - (1461 * _year) / 4 + 31;
            int256 _month = (80 * L) / 2447;
            L = _month / 11;
            _month = _month + 2 - 12 * L;
            _year = 100 * (N - 49) + _year + L;

            uint256 epoch = uint256((_year - 1970) * 12 + _month - 1) / MONTHS_PER_EPOCH;
            if (epoch > type(uint8).max) revert EpochOverflow();
            return uint8(epoch);
        }
    }

    function _epochToTimestamp(uint8 epoch) internal pure returns (uint256) {
        unchecked {
            uint256 months = MONTHS_PER_EPOCH + MONTHS_PER_EPOCH * epoch;
            uint256 year = months / 12 + 1970;
            months = (months % 12) << 4;
            if (((year % 4 == 0) && (year % 100 != 0)) || (year % 400 == 0)) {
                // 0, 31, 60, 91, 121, 152, 182, 213, 244, 274, 305, 335, 366
                months = 0x016E014F0131011200F400D500B600980079005B003C001F0000 >> months;
            } else {
                // 0, 31, 59, 90, 120, 151, 181, 212, 243, 273, 304, 334, 365
                months = 0x016D014E0130011100F300D400B500970078005A003B001F0000 >> months;
            }
            return (
                (months & 0xffff) + 365 * (year - 1970) + (year - 1969) / 4 - (year - 1901) / 100 + (year - 1601) / 400
            ) * SECONDS_PER_DAY;
        }
    }
}
","keccak256":"0xc7b93057e19becf5fdb4d1646120f7609763fc7cd1fd4837eb3712be6dd8c45d","license":"-"},"contracts/libraries/LockData.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

/// @author Coupon Finance
/// @author Modified from Uniswap V4 (https://github.com/Uniswap/v4-core/tree/98680ebc1a654120e995d53a5b10ec6fe153066f)
/// @notice Contains data about pool lockers.
struct LockData {
    /// @notice The current number of active lockers
    uint128 length;
    /// @notice The total number of nonzero deltas over all active + completed lockers
    uint128 nonzeroDeltaCount;
}

/// @dev This library manages a custom storage implementation for a queue
///      that tracks current lockers. The "sentinel" storage slot for this data structure,
///      always passed in as IPoolManager.LockData storage self, stores not just the current
///      length of the queue but also the global count of non-zero deltas across all lockers.
///      The values of the data structure start at OFFSET, and each value is a locker address.
library LockDataLibrary {
    uint256 private constant OFFSET = uint256(keccak256("LockData"));

    /// @dev Pushes a locker onto the end of the queue, and updates the sentinel storage slot.
    function push(LockData storage self, address locker) internal {
        // read current value from the sentinel storage slot
        uint128 length = self.length;
        unchecked {
            uint256 indexToWrite = OFFSET + length; // not in assembly because OFFSET is in the library scope
            /// @solidity memory-safe-assembly
            assembly {
                // in the next storage slot, write the locker
                sstore(indexToWrite, locker)
            }
            // update the sentinel storage slot
            self.length = length + 1;
        }
    }

    /// @dev Pops a locker off the end of the queue. Note that no storage gets cleared.
    function pop(LockData storage self) internal {
        unchecked {
            self.length--;
        }
    }

    function getLock(uint256 i) internal view returns (address locker) {
        unchecked {
            uint256 position = OFFSET + i; // not in assembly because OFFSET is in the library scope
            /// @solidity memory-safe-assembly
            assembly {
                locker := sload(position)
            }
        }
    }

    function getActiveLock(LockData storage self) internal view returns (address locker) {
        return getLock(self.length - 1);
    }
}
","keccak256":"0x6e466532d5cf141145e467f01dfdeb2e7dbaa0304f330e6e7971be06a8da49ca","license":"BUSL-1.1"},"contracts/libraries/PositionManager.sol":{"content":"// SPDX-License-Identifier: -
